from pyecharts import options as opts
from pyecharts.charts import Bar, Funnel, Page, Pie, PictorialBar
from pyecharts.faker import Faker
from bilibili_pandas import d_mid, d_sex, d_sign, d_level, d_following, d_follower, d_archive_view, d_article_view, d_likes


def pictorialBar_1() -> PictorialBar:
    symbols = {
        'level_0': 'image://data:image/png;base64,',
        'level_1': 'image://data:image/png;base64,',
        'level_2': 'image://data:image/png;base64,',
        'level_3': 'image://data:image/png;base64,',
        'level_4': 'image://data:image/png;base64,',
        'level_5': 'image://data:image/png;base64,',
        'level_6': 'image://data:image/png;base64,',
    }
    c = (
        PictorialBar()
        .add_xaxis(list(d_level.keys())[1:])
        .add_yaxis("等级人数",
                   [
                       {"value": d_level['level_0'],
                           "symbol": symbols['level_0']},
                       {"value": d_level['level_1'],
                           "symbol": symbols['level_1']},
                       {"value": d_level['level_2'],
                           "symbol": symbols['level_2']},
                       {"value": d_level['level_3'],
                           "symbol": symbols['level_3']},
                       {"value": d_level['level_4'],
                           "symbol": symbols['level_4']},
                       {"value": d_level['level_5'],
                           "symbol": symbols['level_5']},
                       {"value": d_level['level_6'],
                           "symbol": symbols['level_6']},
                   ],
                   label_opts=opts.LabelOpts(is_show=False),
                   symbol_size=22,
                   symbol_repeat="fixed",
                   is_symbol_clip=True,
                   )
        .reversal_axis()
        .set_series_opts(
            label_opts=opts.LabelOpts(
                position='left', color='black', formatter='{b}:{c}')
        )
        .set_global_opts(
            title_opts=opts.TitleOpts(title="等级分布"),
            legend_opts=opts.LegendOpts(is_show=False),
            xaxis_opts=opts.AxisOpts(is_show=False),
            yaxis_opts=opts.AxisOpts(is_show=False),
        )
    )
    return c


def bar_1() -> Bar:
    c = (
        Bar()
        .add_xaxis(list(d_following.keys())[2:])
        .add_yaxis("关注数", list(d_following.values())[2:])
        .reversal_axis()
        .set_global_opts(
            title_opts=opts.TitleOpts(title='关注数分布'),
        )
        .set_series_opts(
            label_opts=opts.LabelOpts(position='right')
        )
    )
    return c


def bar_2() -> Bar:
    c = (
        Bar()
        .add_xaxis(list(d_follower.keys())[2:])
        .add_yaxis("粉丝数", list(d_follower.values())[2:])
        .reversal_axis()
        .set_global_opts(
            title_opts=opts.TitleOpts(title='粉丝数分布'),
        )
        .set_series_opts(
            label_opts=opts.LabelOpts(position='right')
        )
    )
    return c


def funnel_1() -> Funnel:
    c = (
        Funnel()
        .add("播放量", list(d_archive_view.items())[2:])
        .set_global_opts(title_opts=opts.TitleOpts(title="播放量分布"))
        .set_series_opts(label_opts=opts.LabelOpts(formatter="{b}: {c}"))
    )
    return c


def funnel_2() -> Funnel:
    c = (
        Funnel()
        .add("阅读量", list(d_article_view.items())[2:])
        .set_global_opts(title_opts=opts.TitleOpts(title="阅读量分布"))
        .set_series_opts(label_opts=opts.LabelOpts(formatter="{b}: {c}"))
    )
    return c


def funnel_3() -> Funnel:
    c = (
        Funnel()
        .add("获赞数", list(d_likes.items())[2:])
        .set_global_opts(title_opts=opts.TitleOpts(title="获赞数分布"))
        .set_series_opts(label_opts=opts.LabelOpts(formatter="{b}: {c}"))
    )
    return c


def pie_1() -> Pie:
    c = (
        Pie()
        .add(
            "",
            list(d_sex.items()),
            radius=["20%", "60%"],
            center=["50%", "50%"],
        )
        .set_global_opts(title_opts=opts.TitleOpts(title="Pie-性别分布"))
        .set_colors(['CornflowerBlue', 'LightPink', 'LightGrey'])
        .set_series_opts(label_opts=opts.LabelOpts(formatter="{b}: {c}"))
    )
    return c


def pie_2() -> Pie:
    c = (
        Pie()
        .add(
            "",
            list(d_sign.items()),
            radius=["20%", "60%"],
            center=["50%", "50%"],
        )
        .set_global_opts(title_opts=opts.TitleOpts(title="Pie-签名分布"),
                         legend_opts=opts.LegendOpts(
                             orient="vertical", pos_top="5%", pos_left="5%"),
                         )
        .set_series_opts(label_opts=opts.LabelOpts(formatter="{b}: {c}"))
    )
    return c


def page_simple_layout():
    page = Page(layout=Page.SimplePageLayout)
    page.add(
        pictorialBar_1(),
        bar_1(),
        bar_2(),
        funnel_1(),
        funnel_2(),
        funnel_3(),
        pie_1(),
        pie_2(),
    )
    page.render("processPro/templates/echarts.html")


if __name__ == "__main__":
    page_simple_layout()
